mapper.select:
select

from `device_gps_infos`


        AND `event_time` >= #{startDate}
        AND `event_time` <= #{endDate}


        AND `device_id` = #{deviceId}


        AND `longitude` > 0 AND `latitude` > 0


        AND `msg_type` = #{msgType}


ORDER BY `event_time` desc

